import React, { useState, useEffect } from "react";

const examples = [
  "Boliglån",
  "ROI på utleie",
  "Hvor mye maling trenger jeg?",
  "Konverter kr til dollar",
  "Pinnekjøtt per person"
];

const App = () => {
  const [prompt, setPrompt] = useState("");
  const [response, setResponse] = useState("");
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [typingIndex, setTypingIndex] = useState(0);
  const [typedExample, setTypedExample] = useState("");

  const API_URL =
    process.env.NODE_ENV === "development"
      ? "http://localhost:5000/chat"
      : "https://api-beregne-no.onrender.com/chat";

  // Typewriter-effekt
  useEffect(() => {
    const fullText = examples[typingIndex % examples.length];
    let i = 0;
    const interval = setInterval(() => {
      setTypedExample(fullText.substring(0, i));
      i++;
      if (i > fullText.length) {
        clearInterval(interval);
        setTimeout(() => {
          setTypingIndex((prev) => prev + 1);
        }, 1500);
      }
    }, 100);
    return () => clearInterval(interval);
  }, [typingIndex]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!prompt || isLoading) return;

    setResponse("");
    setError(null);
    setIsLoading(true);

    try {
      const res = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt })
      });

      const data = await res.json();
      if (data.response) {
        setResponse(data.response);
      } else {
        setError("Noe gikk galt. Prøv igjen senere.");
      }
    } catch (err) {
      setError("Feil ved tilkobling til API.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-white px-4">
      <h1 className="text-4xl font-bold mb-4 text-center">Beregne...</h1>
      <form onSubmit={handleSubmit} className="w-full max-w-xl">
        <input
          type="text"
          className="w-full px-4 py-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring focus:border-blue-500 transition"
          placeholder={typedExample || "Hva vil du beregne?"}
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
        />
        <button
          type="submit"
          disabled={isLoading}
          className={`mt-4 w-full py-2 px-4 rounded-lg transition ${
            isLoading
              ? "bg-gray-400 cursor-not-allowed"
              : "bg-black text-white hover:bg-gray-800"
          }`}
        >
          {isLoading ? "Beregner..." : "Beregn"}
        </button>
      </form>

      {response && (
        <div className="mt-6 max-w-2xl bg-gray-100 p-4 rounded-lg shadow text-left whitespace-pre-wrap">
          {response}
        </div>
      )}

      {error && (
        <div className="mt-6 text-red-500 font-semibold">{error}</div>
      )}
    </div>
  );
};

export default App;
