import React, { useEffect, useState } from "react";

const examples = [
  "Boliglån",
  "ROI på utleie",
  "Yield på næringseiendom",
  "Konverter kWh til kroner",
  "Hvor mye pinnekjøtt per person?"
];

function App() {
  const [placeholder, setPlaceholder] = useState("");
  const [exampleIndex, setExampleIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);
  const [query, setQuery] = useState("");
  const [response, setResponse] = useState("");
  const [isTyping, setIsTyping] = useState(true);

  // Typewriter-effekt
  useEffect(() => {
    if (!isTyping) return;

    const currentText = examples[exampleIndex];
    if (charIndex < currentText.length) {
      const timeout = setTimeout(() => {
        setPlaceholder(currentText.slice(0, charIndex + 1));
        setCharIndex((prev) => prev + 1);
      }, 100);
      return () => clearTimeout(timeout);
    } else {
      const timeout = setTimeout(() => {
        setCharIndex(0);
        setExampleIndex((prev) => (prev + 1) % examples.length);
      }, 2000);
      return () => clearTimeout(timeout);
    }
  }, [charIndex, exampleIndex, isTyping]);

  const handleSearch = async () => {
    if (!query.trim()) return;
    setResponse("Tenker...");

    try {
      const res = await fetch("https://api.beregne.no/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt: query })
      });
      const data = await res.json();
      setResponse(data.response || "Kunne ikke hente svar.");
    } catch (err) {
      setResponse("Noe gikk galt. Prøv igjen senere.");
    }
  };

  return (
    <div className="h-screen bg-white flex flex-col items-center justify-center px-4 text-center">
      <input
        type="text"
        className="border border-gray-300 rounded-xl px-6 py-3 w-full max-w-xl focus:outline-none text-lg shadow-sm"
        placeholder={placeholder}
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        onKeyDown={(e) => e.key === "Enter" && handleSearch()}
      />
      <button
        onClick={handleSearch}
        className="mt-4 bg-black text-white px-5 py-2 rounded-xl text-sm hover:bg-gray-800"
      >
        Beregn
      </button>
      {response && (
        <div className="mt-6 max-w-2xl text-left text-gray-700 whitespace-pre-wrap">
          {response}
        </div>
      )}
    </div>
  );
}

export default App;
